import React from 'react';
import {Form, FormGroup} from 'reactstrap';
import {Link, Redirect} from 'react-router-dom';

//this will save all our array of cats

class ApartmentNew extends React.Component {
    constructor(){
        super()
        this.state = {
            success: false,
            form: {
                street: '',
                city: '',
                state: '',
                postal_code: '',
                country: '',
                price: '',
                manager_name: '',
                manager_phone: '',
                contact_hours: '',
                description: '',
                user_id: ''
            }
        }
    }
    handleSubmit = () => {
        this.props.onSubmit(this.state.form)
        .then(() => {
            this.setState({success:true})
        })
    }
    handleChange = (event) => {
        let {form} = this.state
        form[event.target.street]= event.target.value
        this.setState({form: form})
    }
    render(){
        let {street, city, state, postal_code, country, price, manager_name, manager_phone, contact_hours, description, user_id} = this.state.form
    return(
        <div>
            <h1>New Component: HEYY Apartment NEW</h1>
            <Form>
              <fieldset>
                <legend>Add a new apartment</legend>
                    <FormGroup className="form-group">
                        <label for="exampleInputEmail1">Street Address</label>
                            <input name="name" type="text" className="form-control" id="street" aria-describedby="emailHelp" placeholder="Street Address" value={street} onChange={this.handleChange}/>
                    </FormGroup>


                <FormGroup className="form-group">
                  <label for="exampleTextarea">Description</label>
                  <textarea className="form-control"  rows="3" id="description" type="text" value={description} name="bio" onChange={this.handleChange}></textarea>
                </FormGroup>

                <Link to={"/apartments"}>
                <button onClick={this.handleSubmit} type="submit" className="btn btn-primary">Add Apartment!</button></Link>
              </fieldset>
            </Form>

            {this.state.success &&
                <Redirect to="/apartments" />
            }
        </div>
    )
    }
}

export default ApartmentNew;
